CREATE TABLE IF NOT EXISTS `user` (`id` INTEGER NOT NULL, `full_name` TEXT NOT NULL, `image_path` TEXT NOT NULL, PRIMARY KEY(`id`));


CREATE TABLE IF NOT EXISTS `message` (`id` INTEGER NOT NULL, `user_id` INTEGER NOT NULL, `message` TEXT NOT NULL, `is_forward` INTEGER NOT NULL, `created_at` TEXT NOT NULL, PRIMARY KEY(`id`), FOREIGN KEY(`user_id`) REFERENCES `user`(`id`) ON UPDATE NO ACTION ON DELETE NO ACTION );

